```js
var bg = $("#background");
var mg = $("#midground");
var fg = $("#foreground");

$(document).keydown(function(e) {
  if (e.which === 39) { //right arrow key
    bg.animate({ backgroundPosition: "-=1px" }, 0, "linear" );
    mg.animate({ backgroundPosition: "-=10px" }, 0, "linear" );
    fg.animate({ backgroundPosition: "-=20px" }, 0, "linear" );
  }
});
```

