$(document).keydown(function(e) {

  //37 = left arrow | 39 = right arrow
  if (e.keyCode === 37 || e.keyCode === 39) {

    message.fadeOut("slow");

    if (!scroller.is(":animated")) {
      scroller.stop().animate({
        left: (e.keyCode === 37) ? 0 : -(scroller.width() - prox.width())
      }, 6000, "linear");
    }
  }
}).keyup(function() {
  scroller.stop();
});
