    $("body").on("marquee-ready", "#marquee", function() {

      var marquee = $(this),
        postLink = marquee.find("a").eq(0);
        width = postLink.width() + parseInt(postLink.css("marginRight")),
        time = 15 * width;

      postLink.animate({
        marginLeft: "-=" + width	
      }, time, "linear", function() {
        $(this).css({
          marginLeft: 0
        }).appendTo(marquee.find("div").eq(0));
        marquee.trigger("marquee-ready");
      });
    });
