```js
$(document).ready(function($){
  var dragOpts = {
    containment: "#maze"
  },
  dropOpts = {
    tolerance: "touch",
    over: function(e, ui) {
      $("#drag").draggable("destroy").remove();
      $("<div />", {
        id: "drag",
        css: {
          left: 0,
          top: 0
        }
      }).appendTo("#maze");
      $("#drag").draggable(dragOpts);
    }   
  },
  endOpts = {
    over: function(e, ui) {
      $("#drag").draggable("destroy").remove();
      alert("Woo! You did it!");
    }
  };
  for (var x = 1; x < 47; x++) {
    $("<div />", {
      id: "a" + x,
      class: "wall"
    }).appendTo("#maze");
  }
  $("#drag").draggable(dragOpts);
  $(".wall").droppable(dropOpts);
  $("#end").droppable(endOpts);
});
